function PROXY:Init(mat, kv)	local var_result = mat:FindVar(kv:GetString("resultVar") or "")	if not var_result then return false end		self.Result = var_result		return trueendfunction PROXY:OnBind(ent)	if not self.Result then return end		if ent:GetProxyVar("IsCommunityWeapon") then		self.Result:SetFloatValue(1.0)	else		self.Result:SetFloatValue(0.0)	endendfunction PROXY:GetMaterial()	if not self.Result then return end		return self.Result:GetOwningMaterial()end