local DefaultColor = {0.0, 0.0, 0.0}local TempColor = {0.0, 0.0, 0.0}local inv255 = 1/255function PROXY:Init(mat, kv)	local var_result = mat:FindVar(kv:GetString("resultVar") or "")	if not var_result then return false end		self.Result = var_result		return trueendfunction PROXY:OnBind(ent)	if not self.Result then return end		if ent.ProxyItemTint then		TempColor[1] = ent.ProxyItemTint[1]		TempColor[2] = ent.ProxyItemTint[2]		TempColor[3] = ent.ProxyItemTint[3]				self.Result:SetVecValue(TempColor, 3)	else		self.Result:SetVecValue(DefaultColor, 3)	endendfunction PROXY:GetMaterial()	if not self.Result then return end		return self.Result:GetOwningMaterial()end