local DefaultGlowColor = {1.0, 1.0, 1.0}--[[local ]]GlowColorTable = {	{	-- red		{14.0, 1.0, 1.0},	-- crit		{9.0, 4.0, 1.0},	-- minicrit	},	{	-- blue		{1.0, 4.0, 14.0},	-- crit		{2.0, 9.0, 9.0},	-- minicrit	},}function PROXY:Init(mat, kv)	local var_result = mat:FindVar(kv:GetString("resultVar") or "")	if not var_result then return false end		self.Result = var_result		return trueendfunction PROXY:OnBind(ent)	if not self.Result or not IsValid(ent) then return end		local owner = (IsValid(ent.Player) and ent.Player) or ent		local t = owner:GetProxyVar("CritTeam")	local s = owner:GetProxyVar("CritStatus")	if s and t and GlowColorTable[t] and GlowColorTable[t][s] then		self.Result:SetVecValue(GlowColorTable[t][s], 3)	else		self.Result:SetVecValue(DefaultGlowColor, 3)	endendfunction PROXY:GetMaterial()	if not self.Result then return end		return self.Result:GetOwningMaterial()end