local SNIPERSCOPE_MIN = -0.75local SNIPERSCOPE_MAX = -2.782local SNIPERSCOPE_SCALE = 0.4function PROXY:Init(mat, kv)	local var_result = mat:FindVar(kv:GetString("resultVar") or "")	if not var_result then return false end		self.Result = var_result		return trueendfunction PROXY:OnBind(ent)	if not self.Result then return end		local e = LocalPlayer().ProxySniperCharge or 0		local m = Matrix()	m:Scale(Vector(1,SNIPERSCOPE_SCALE,1))	m:Translate(Vector(0,Lerp(e/100,SNIPERSCOPE_MIN,SNIPERSCOPE_MAX),0))	self.Result:SetMatrixValue(m)endfunction PROXY:GetMaterial()	if not self.Result then return end		return self.Result:GetOwningMaterial()end