local JaratedColor = {2.0, 2.0, 0.7}local DefaultColor = {1.0, 1.0, 1.0}function PROXY:Init(mat, kv)	local var_result = mat:FindVar(kv:GetString("resultVar") or "")	if not var_result then return false end		self.Result = var_result		return trueendfunction PROXY:OnBind(ent)	if not self.Result then return end		local owner = ent:GetOwner()	if not IsValid(owner) then		owner = (IsValid(ent.Player) and ent.Player) or ent	end		if owner:GetProxyVar("Jarated") then		self.Result:SetVecValue(JaratedColor, 3)	else		self.Result:SetVecValue(DefaultColor, 3)	endendfunction PROXY:GetMaterial()	if not self.Result then return end		return self.Result:GetOwningMaterial()end