local TempVector = {1.0, 1.0, 1.0}function PROXY:Init(mat, kv)	local var_factor = mat:FindVar("$cloakfactor")	local var_tint = mat:FindVar("$cloakcolortint")	local var_refract = mat:FindVar("$refractamount")		self.Factor = var_factor	self.Tint = var_tint	self.Refract = var_refract		return trueendfunction PROXY:OnBind(ent)	if not self.Result then return end	if not IsValid(ent) then return end		local owner = ent:GetOwner()	if not IsValid(owner) then		owner = (IsValid(ent.Player) and ent.Player) or ent	end		self.Factor:SetFloatValue(ent:GetProxyVar("CloakLevel") or 0)	self.Refract:SetFloatValue(ent:GetProxyVar("CloakRefract") or 0.5)		local tint = ent:GetProxyVar("CloakTint")		if tint then		TempVector[1] = tint[1]		TempVector[2] = tint[2]		TempVector[3] = tint[3]	else		TempVector[1] = 1.0		TempVector[2] = 1.0		TempVector[3] = 1.0	end	self.Tint:SetVecValue(TempVector, 3)endfunction PROXY:GetMaterial()	if not self.Factor then return end		return self.Factor:GetOwningMaterial()end